import { Component, OnInit } from '@angular/core';
import { ProfileService } from '../services/profile.service';
import { Profile } from '../interfaces/profile.interface';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  profile?: Profile;

  constructor(private profileService: ProfileService) {}

  ngOnInit(): void {
    this.loadProfile();
  }

  loadProfile(): void {
    this.profileService.getProfile().subscribe({
      next: (data) => {
        this.profile = data;
      },
      error: (error) => {
        console.error('Error loading profile:', error);
      }
    });
  }

  handleNavigate(type: string): void {
    const social = this.profile?.socialLinks.find(link => link.type === type);
    if (social?.url) {
      window.open(social.url, '_blank');
    }
  }
}
<div class="flex justify-center items-center gap-6 bg-[#f1f1f1] min-h-[100vh]">
  <img
    class="h-[20rem] w-[20rem] rounded-full"
    [src]="profile?.avatarUrl || '/assets/avatar.jpeg'"
    [alt]="profile?.name || 'Avatar'"
  />
  <div class="flex flex-col gap-4">
    <h1 class="text-2xl">{{ profile?.name || 'Loading...' }}</h1>
    <p class="text-[#606060]">{{ profile?.githubAccount || 'Loading...' }}</p>
    <p class="text-[#606060]">{{ profile?.role || 'Loading...' }}</p>
    <div class="flex gap-2">
      <div
        *ngFor="let social of profile?.socialLinks"
        (click)="handleNavigate(social.type)"
        class="link-icon flex justify-center items-center w-12 h-12 rounded-full bg-transparent transition-transform duration-300 hover:cursor-pointer hover:bg-[#d8d8d8] hover:scale-110"
      >
        <img class="w-12 h-12" [src]="social.iconUrl" [alt]="social.type" />
      </div>
    </div>
  </div>
</div>
