import { Component } from "@angular/core";

interface SocialLink {
  name: string;
  url: string;
}

@Component({
  selector: "app-home",
  templateUrl: "./home.component.html",
  styleUrls: ["./home.component.scss"],
})
export class HomeComponent {
  informationURL: SocialLink[] = [
    {
      name: "github",
      url: "https://github.com/lcaohoanq",
    },
    {
      name: "ig",
      url: "https://www.instagram.com/lcaohoanq/",
    },
  ];

  // Convert the array to a map
  informationURLMap: { [key: string]: string } = this.informationURL.reduce(
    (acc, item) => {
      acc[item.name] = item.url;
      return acc;
    },
    {},
  );

  handleNavigate(name: string): void {
    const url = this.informationURLMap[name];

    if (url) {
      window.open(url, "_blank");
    }
  }
}
<div class="flex justify-center items-center gap-6 bg-[#f1f1f1] min-h-[100vh]">
  <img
    class="h-[20rem] w-[20rem] rounded-full"
    src="/assets/avatar.jpeg"
    alt="Avatar"
  />
  <div class="flex flex-col gap-4">
    <h1 class="text-2xl">Luu Cao Hoang</h1>
    <p class="text-[#606060]">GitHub account is lcaohoanq</p>
    <p class="text-[#606060]">Fullstack Web Developer</p>
    <div class="flex gap-2">
      <div
        (click)="handleNavigate('github')"
        class="link-icon flex justify-center items-center w-12 h-12 rounded-full bg-transparent transition-transform duration-300 hover:cursor-pointer hover:bg-[#d8d8d8] hover:scale-110"
      >
        <img class="w-12 h-12" src="/assets/github.png" alt="Github" />
      </div>
      <div
        (click)="handleNavigate('ig')"
        class="link-icon flex justify-center items-center w-12 h-12 rounded-full bg-transparent transition-transform duration-300 hover:cursor-pointer hover:bg-[#d8d8d8] hover:scale-110"
      >
        <img class="w-12 h-12" src="/assets/ig.png" alt="Instagram" />
      </div>
    </div>
  </div>
</div>
